# Functions used in my algo
# Function to extract YouTube Video Id
def YTvideo (string, video_ids):
    a =''
    count = 0
    for i in string:
        count += 1
        if (count < 33 or count>43):
            continue
        else:
            a = a + i
    video_ids.append(a)
# Function to extract Video Details
def get_video_details(youtube, video_ids):
    all_video_stats = []
    for i in range(0, len(video_ids), 50):
        request = youtube.videos().list(
                    part='snippet,statistics',
                    id=','.join(video_ids[i:i+50]))
        response = request.execute()
        
        for video in response['items']:
            video_stats = dict(Title = video['snippet']['title'],
                               Description = video['snippet']['description']
                               )
            all_video_stats.append(video_stats)
    return all_video_stats
# Function to remove emoji
def remove_emoji(text):
    text = clean(text, no_emoji=True)
    return text
# Function to remove numbers and punctuations
def remove_punc(test_str):
    new_string = test_str.translate(str.maketrans('', '', string.punctuation))
    final_str = ''.join([i for i in new_string if not i.isdigit()])
    return final_str
# Function to find video keywords
def keyword(f3):
    data = f3
    word = data.split()
    dict = {}
    for j in word:
        if j in dict.keys():
            dict[j] += 1
        else:
            dict[j] = 1
    return dict

